#include <iostream>#include <sstream>#include <string>using namespace std;int main(){	cout << "Record Output?" << endl;	char recout;	cin >> recout;	ostringstream pipe(ostringstream::out);	if (recout == 'y') {		system("mkdir output" );		cout << "filename :" << endl;		string fname;		cin >> fname;		pipe << " | tee output/" << fname << ".txt";		system(string("touch output/" + fname + ".txt").c_str());	} 		cout << "Run skimmer?";	char recskim;	cin >> recskim;		cout << "Run MLP Setup?";	char recset;	cin >> recset;	cout << "Run MLP Nets?";	char recnet;	cin >> recnet;		if (recskim == 'y') {		system(string("make run -C skimmer" + pipe.str()).c_str());	} 		if (recset == 'y') {		system(string("make runsetup -C mlp" + pipe.str()).c_str());	} 	if (recnet == 'y') {		system(string("make run -C mlp" + pipe.str()).c_str());	} 	return 0;}